import React, { useState } from 'react'
import AddTask from './AddTask'
import Tasks from './Tasks'
import { Box, Heading, VStack } from '@chakra-ui/react'

function Todo() {
    const [tasks, editTasks] = useState([{id: 0, text: 'text'}]);

    function updateTasks(newTask){
        editTasks([...tasks, newTask]);
    }

    function deleteTask(taskId) {
        editTasks(tasks.filter((task) => {
            return task.id !== taskId;
        }))
        console.log('test');
    }
    return (
        <VStack border='1px' borderColor='gray.400' borderRadius='base' p='5'  my={['150']} w={[
            '100%',
            '300px',
            '600px'
        ]}>
            <Box w='100%'>
                <Heading textAlign='center' pb='3'>Todo</Heading>
            </Box>
            <AddTask updateTasks={updateTasks}/>
            <Tasks tasks={tasks} deleteTask={deleteTask}/>
        </VStack>
    )
}

export default Todo
