import React from "react";
import { Button, Box, Flex, Text } from "@chakra-ui/react";
import { DeleteIcon } from "@chakra-ui/icons";

function Task(props) {
  const { text } = props;
  return (
    <Flex alignItems='center' py='2'>
      <Box cursor='pointer' flex='1 0 0'>
        <Text fontSize='xl' whiteSpace='initial'>{text}</Text>
      </Box>
      <Box>
        <Button colorScheme="red">
          <DeleteIcon />
        </Button>
      </Box>
    </ Flex>
  );
}

export default Task;
