  const [tasks, editTasks] = useState([]);
  const [tasksIndex, incrementIndex] = useState(0);

  async function addTask(text) {
    const newTask = { id: tasksIndex, text: text };
    editTasks([...tasks, newTask]);
    incrementIndex(tasksIndex + 1);
