import React, { useState } from 'react'
import AddTask from './AddTask'
import Tasks from './Tasks'

function Todo() {
    const [tasks, addTask] = useState(['test']);

    function updateTasks(newTask){
        addTask([...tasks, newTask]);
    }
    return (
        <>
            <h1>Todo</h1>
            <AddTask updateTasks={updateTasks}/>
            <Tasks tasks={tasks}/>
        </>
    )
}

export default Todo
